   SELECT
    id,canteen_id,tenant_id,canteen_name,description,is_assess,address,canteen_type,imageurl,
    ROUND(
        6378.138 * 2 * ASIN(
            SQRT(
                POW(
                    SIN(
                        (
                             #{latitude,jdbcType=DOUBLE} * PI() / 180 - latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS(#{latitude,jdbcType=DOUBLE} * PI() / 180) * COS(latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{longitude,jdbcType=DOUBLE} * PI() / 180 - longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
            )
        ) * 1000
    ) AS distance
FROM
   t_canteen where is_delete=0

