mapper.select:
SELECT
FROM y_banner WHERE 1 = 1 and is_delete = 1
and banner_title LIKE concat(concat('%',#{keyword}),'%')
AND machine_id = #{machineId}
limit #{offset},#{pageSize}

SELECT count(1) FROM y_banner WHERE 1 = 1 and is_delete = 1
and banner_title LIKE concat(concat('%',#{keyword}),'%')
AND machine_id = #{machineId}
