mapper.select:
SELECT

FROM
y_img
WHERE
is_delete = 1

  and  res_name LIKE concat(concat('%',#{keyword}),'%')




  AND res_type_id  = #{resTypeId}



  AND date_format(update_time,'%Y-%m-%d')=#{updateTime}

limit #{offset},#{pageSize}

SELECT COUNT(1) FROM y_img WHERE is_delete = 1
and res_name LIKE concat(concat('%',#{keyword}),'%')
AND res_type_id = #{resTypeId}
AND date_format(update_time,'%Y-%m-%d')=#{updateTime}
