mapper.select:
SELECT

FROM
y_page_img
WHERE 1 = 1
and is_delete = 1

  and  page_img_name LIKE concat(concat('%',#{keyword}),'%')


  AND page_id = #{pageId}



  AND machine_id = #{machineId}

order by sequence
limit #{offset},#{pageSize}

SELECT count(1) FROM y_page_img WHERE 1 = 1 and is_delete = 1
and page_img_name LIKE concat(concat('%',#{keyword}),'%')
AND page_id = #{pageId}
AND machine_id = #{machineId}

SELECT page_img_url as pageImgUrl FROM y_page_img WHERE 1 = 1 and is_delete = 1
AND page_id = #{pageId}
AND machine_id = #{machineId}
