mapper.select:
SELECT

FROM
y_res_type
WHERE 1 = 1

  and  res_type_name LIKE concat(concat('%',#{keyword}),'%')


  AND page_id = #{pageId}


  AND machine_id = #{machineId}

limit #{offset},#{pageSize}

SELECT count(1) FROM y_res_type WHERE 1 = 1
and res_type_name LIKE concat(concat('%',#{keyword}),'%')
AND page_id = #{pageId}
AND machine_id = #{machineId}
