mapper.select:
select c.accountbalance
from t_associator AS a LEFT JOIN dh_card c ON  a.cardId = c.cardid
where a.associator_id = #{associatorId,jdbcType=VARCHAR}

SELECT count(co.id) AS totalCount,sum(co.money) AS totalMoney from (select rb.id as id, rb.money as money from dh_associator_refundbill rb LEFT JOIN t_associator a ON rb.associator_id = a.associator_id LEFT JOIN t_associator_type type ON a.associator_type_id = type.id LEFT JOIN dh_card card ON card.cardid = a.cardid LEFT JOIN t_associator_canteen tc ON a.associator_id = tc.associator_id LEFT JOIN t_canteen c ON c.canteen_id = tc.canteen_id where 1=1 AND DATE_FORMAT(rb.refund_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND a.account LIKE concat(concat('%',#{keyword}),'%') OR card.carduid LIKE concat(concat('%',#{keyword}),'%')
and c.tenant_id = #{user.tenantId,jdbcType=VARCHAR} AND
c.canteen_id=#{can,jdbcType=VARCHAR}
AND c.canteen_id=#{canteenId,jdbcType=VARCHAR}
GROUP BY rb.refund_time) AS co

select rb.id AS id,rb.money AS money,rb.associator_id AS associator_id,rb.user_id AS user_id, rb.refund_time AS refund_time,rb.refund_time AS refundTimeStr,rb.refund_type AS refund_type,CASE rb.refund_type WHEN 0 THEN '线下退款' END AS refundTypeName, a.id AS a_id,a.associator_id AS a_associator_id,a.account,a.cardId,a.telephone,a.associator_type_id, type.name AS associatorTypeName,card.carduid AS cardUid from dh_associator_refundbill rb LEFT JOIN t_associator a ON rb.associator_id = a.associator_id LEFT JOIN t_associator_type type ON a.associator_type_id = type.id LEFT JOIN dh_card card ON card.cardid = a.cardid LEFT JOIN t_associator_canteen tc ON a.associator_id = tc.associator_id LEFT JOIN t_canteen c ON c.canteen_id = tc.canteen_id where 1=1 AND DATE_FORMAT(rb.refund_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND a.account LIKE concat(concat('%',#{keyword}),'%') OR card.carduid LIKE concat(concat('%',#{keyword}),'%')
and c.tenant_id = #{user.tenantId,jdbcType=VARCHAR} AND
c.canteen_id=#{can,jdbcType=VARCHAR}
AND c.canteen_id=#{canteenId,jdbcType=VARCHAR}
GROUP BY rb.refund_time limit #{offSet},#{pageSize}

select sum(rb.money) from dh_associator_refundbill rb LEFT JOIN t_associator a ON rb.associator_id = a.associator_id LEFT JOIN t_associator_type type ON a.associator_type_id = type.id LEFT JOIN dh_card card ON card.cardid = a.cardid LEFT JOIN t_associator_canteen tc ON a.associator_id = tc.associator_id LEFT JOIN t_canteen c ON c.canteen_id = tc.canteen_id where 1=1 AND DATE_FORMAT(rb.refund_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND a.account LIKE concat(concat('%',#{keyword}),'%') OR card.carduid LIKE concat(concat('%',#{keyword}),'%')
and c.tenant_id = #{user.tenantId,jdbcType=VARCHAR} AND
c.canteen_id=#{can,jdbcType=VARCHAR}
AND c.canteen_id=#{canteenId,jdbcType=VARCHAR}
