mapper.insert:

insert into t_recharge_log (id, out_trade_no, associator_id,
recharge_money, recharge_type, operation_time,
remark, safetyone, safetytwo
)
values (#{id,jdbcType=INTEGER}, #{outTradeNo,jdbcType=VARCHAR}, #{associatorId,jdbcType=VARCHAR},
#{rechargeMoney,jdbcType=FLOAT}, #{rechargeType,jdbcType=SMALLINT}, #{operationTime,jdbcType=TIMESTAMP},
#{remark,jdbcType=VARCHAR}, #{safetyone,jdbcType=VARCHAR}, #{safetytwo,jdbcType=VARCHAR}
)

mapper.select:
select * from t_recharge_log where associator_id=#{associatorId}

    and (operation_time between date_format(#{startTime},'%Y-%m-%d %H:%i:%s') and
    date_format(#{endTime},'%Y-%m-%d %H:%i:%s'))


    limit #{offset},#{pageSize}
;

select count(*) from t_recharge_log where associator_id=#{associatorId}

    and (operation_time between date_format(#{startTime},'%Y-%m-%d %H:%i:%s') and
    date_format(#{endTime},'%Y-%m-%d %H:%i:%s'));
