mapper.select:
select
t.id,t.tenant_id,t.tenant_name,t.level,t.officer,t.telephone,t.imageurl,t.address,t.longitude,t.latitude,t.create_user,t.create_time
from t_tenant t where t.is_delete=0 and t.tenant_id= #{tenantId,jdbcType=VARCHAR}

    AND t.tenant_name like concat('%',#{name},'%')

order by id desc

    limit #{offset},#{pageSize}
;
