mapper.select:
SELECT
FROM y_pdf WHERE 1 = 1 and is_delete = 1
and pdf_name LIKE concat(concat('%',#{keyword}),'%')
AND res_type_id = #{resTypeId}
AND res_type_id in (select res_type_id FROM y_res_type where machine_id in (select ym.id from y_machine ym INNER JOIN t_user tu ON ym.canteen_id = tu.canteen_id where 1=1
and ym.canteen_id in
#{item}
) )
limit #{offset},#{pageSize}

SELECT count(1) FROM y_pdf WHERE 1 = 1 and is_delete = 1
and pdf_name LIKE concat(concat('%',#{keyword}),'%')
AND res_type_id = #{resTypeId}
AND res_type_id in (select res_type_id FROM y_res_type where machine_id in (select ym.id from y_machine ym INNER JOIN t_user tu ON ym.canteen_id = tu.canteen_id where 1=1
and ym.canteen_id in
#{item}
) )

SELECT yp.pdf_url from y_pdf yp,y_res_type yrt,y_machine ym where yrt.machine_id = ym.id and yrt.res_type_id = yp.res_type_id
AND yrt.res_type_id = #{resTypeId}
AND yrt.machine_id = #{machineId}

select yp.pdf_url from y_page_img ypi,y_pdf yp where ypi.page_img_name = yp.pdf_name
AND ypi.id = #{pageImgId}
limit 1
