mapper.select:
SELECT
FROM y_video WHERE 1 = 1 and is_delete = 1
and video_name LIKE concat(concat('%',#{keyword}),'%')
AND page_id = #{pageId}
AND machine_id = #{machineId}
and machine_id in (select ym.id from y_machine ym INNER JOIN t_user tu ON ym.canteen_id = tu.canteen_id where 1=1
and ym.canteen_id in
#{item}
)
limit #{offset},#{pageSize}

SELECT count(1) FROM y_video WHERE 1 = 1 and is_delete = 1
and video_name LIKE concat(concat('%',#{keyword}),'%')
AND page_id = #{pageId}
AND machine_id = #{machineId}
and machine_id in (select ym.id from y_machine ym INNER JOIN t_user tu ON ym.canteen_id = tu.canteen_id where 1=1
and ym.canteen_id in
#{item}
)

SELECT id as id, video_name as name, img_url as img FROM y_video WHERE 1 = 1 and is_delete = 1
AND page_id = #{pageId}
AND machine_id = #{machineId}

SELECT video_url as videoUrl FROM y_video WHERE 1 = 1 and is_delete = 1
AND page_id = #{pageId}
AND machine_id = #{machineId}
limit 1

SELECT video_url as videoUrl FROM y_video WHERE 1 = 1 and is_delete = 1
AND id = #{id}
limit 1
