mapper.select:
select ddr.id,ddr.canteenid,ddr.breakfast,ddr.lunch,ddr.dinner,ddr.disktypeid as diskTypeId,ddt.name as diskName,ddr.recipetime,bf.recipename as breakfastName,lc.recipename as lunchName,dn.recipename as dinnerName,ddr.createuser as createUser,ddr.createtime as createTime from dh_disk_recipe ddr left join breakfast bf on bf.disktypeid=ddr.disktypeid left join lunch lc on lc.disktypeid=ddr.disktypeid left join dinner dn on dn.disktypeid =ddr.disktypeid left join dh_disk_type ddt on ddt.id=ddr.disktypeid where 1=1
and ddr.canteenid=#{canteenId}
and ddt.name like concat(concat('%',#{keyword}),'%')
limit #{offset},#{pageSize} ;

select count(*) from dh_disk_recipe ddr left join breakfast bf on bf.disktypeid=ddr.disktypeid left join lunch lc on lc.disktypeid=ddr.disktypeid left join dinner dn on dn.disktypeid =ddr.disktypeid left join dh_disk_type ddt on ddt.id=ddr.disktypeid where 1=1
and ddr.canteenid=#{canteenId}
and ddt.name like concat(concat('%',#{keyword}),'%')
