mapper.select:
select  count(1)  from t_good_planitems items INNER JOIN  t_good good ON items.good_id = good.good_id LEFT JOIN t_supplier s ON s.supplier_id = good.supplier_id
where items.good_plan_id = #{goodPlanId} AND items.is_delete = 0

  AND good.good_name LIKE #{keyword}


  AND good.category_id = #{categoryId}

select items.id,items.good_id,items.good_plan_id,items.surplus,items.price_day,items.market_price_day,items.is_hot,
   good.id g_id,good.good_id g_good_id,good.good_name g_good_name,good.imageurl g_imageurl,good.bigimageurl g_bigimageurl,good.price g_price,good.market_price g_market_price,good.standard g_standard,
   s.id s_id,s.supplier_id s_supplier_id,s.name s_name
from t_good_planitems items INNER JOIN  t_good good ON items.good_id = good.good_id  LEFT JOIN t_supplier s ON s.supplier_id = good.supplier_id
where items.good_plan_id = #{goodPlanId} AND items.is_delete = 0 AND good.status = 0 AND good.is_delete = 0

  AND good.good_name LIKE #{keyword}


  AND good.category_id = #{categoryId}

ORDER BY good.category_id ASC,items.is_hot DESC,good.create_time DESC
limit #{offSet},#{pageSize}

select id,good_id,good_plan_id,price_day from t_good_planitems where 1=1 and
good_plan_id=#{goodPlanId,jdbcType=VARCHAR}
GROUP BY good_id
