mapper.update:
update t_good_plan
set update_user = #{updateUser,jdbcType=VARCHAR},
update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select count(id) from t_good_plan WHERE 1=1 AND is_delete = 0

    AND canteen_id = #{canteenId}


    AND

        canteen_id=#{can,jdbcType=VARCHAR}



    AND picking_time BETWEEN #{starttime} AND #{endtime}


    AND picking_time >= #{now}

select id, good_plan_id, is_delete, picking_time,comment,canteen_id,create_user, create_time
from t_good_plan WHERE 1=1 AND is_delete = 0

    AND canteen_id = #{canteenId}


    AND

        canteen_id=#{can,jdbcType=VARCHAR}



    AND picking_time BETWEEN #{starttime} AND #{endtime}


    AND picking_time >= #{now}

ORDER BY picking_time ASC
limit #{offSet},#{pageSize};

select plan.id,plan.good_plan_id,plan.picking_time,plan.canteen_id, items.id it_id,items.good_id it_good_id,items.surplus it_surplus,items.price_day it_price_day,items.market_price_day it_market_price_day,items.is_hot it_is_hot, good.id g_id,good.good_id g_good_id,good.good_name g_good_name,good.price g_price,good.market_price g_market_price,good.imageurl g_imageurl,good.standard g_standard from t_good_plan plan LEFT JOIN t_good_planitems items ON plan.good_plan_id = items.good_plan_id LEFT JOIN t_good good ON items.good_id = good.good_id WHERE 1=1 AND plan.is_delete = 0
AND plan.good_plan_id LIKE #{keyword}
AND plan.canteen_id = #{canteenId}
AND plan.picking_time BETWEEN #{startdate} AND #{enddate} ORDER BY plan.picking_time ASC

select plan.*,items.good_id it_good_id,good.good_name g_good_name,good.imageurl g_imageurl,good.category_id
g_category_id,category.name c_name
from t_good_plan plan INNER JOIN t_good_planitems items ON plan.good_plan_id = items.good_plan_id
INNER JOIN t_good good ON items.good_id = good.good_id
INNER JOIN t_good_category category ON good.category_id = category.id
WHERE 1=1

    AND plan.canteen_id = #{canteenId}


    AND items.sale_time BETWEEN #{startSaleTime} AND #{endSaleTime}


    AND good.category_id = #{categoryId}

mapper.update:
update t_good_plan
set is_delete = 1,
  update_user = #{updateUser,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id,good_plan_id,is_delete from t_good_plan WHERE 1=1 AND is_delete = 0

    AND canteen_id = #{canteenId}

AND DATE_FORMAT(picking_time, "%Y-%m-%d") = #{pickingTime}

select id,good_plan_id,canteen_id from t_good_plan
where is_delete=0 AND picking_time BETWEEN #{startDate,jdbcType=TIMESTAMP}
and #{endDate,jdbcType=TIMESTAMP} AND

    canteen_id=#{can,jdbcType=VARCHAR}
