mapper.insert:

insert into dh_disk_label (id, labeluid, canteenid,
recipeid, disktypeid, exw_time,
start_time, end_time, vali,temp
)
values (#{id,jdbcType=INTEGER}, #{labelUid,jdbcType=VARCHAR}, #{canteenId,jdbcType=VARCHAR},
#{recipeId,jdbcType=VARCHAR}, #{diskTypeId,jdbcType=INTEGER}, #{exwTime,jdbcType=DATE},
#{startTime,jdbcType=DATE}, #{endTime,jdbcType=DATE}, #{valid,jdbcType=TINYINT},#{temp,jdbcType=TINYINT}
)

mapper.select:
select ddl.labeluid,tr.recipe_name as recipeName,ddl.exw_time
from dh_disk_label ddl,t_recipe tr
where ddl.recipeid=tr.recipe_id and ddl.canteenid=#{canteenId} and ddl.disktypeid=#{diskTypeId}
