mapper.select:
select count(ref.id) AS totalCount,sum(ref.money) AS totalMoney from dh_member_refundbill ref
LEFT JOIN dh_members mem ON mem.member_id = ref.member_id
LEFT JOIN dh_member_card card ON mem.cardid = card.cardid
where 1=1 AND mem.is_delete = 0 AND DATE_FORMAT(ref.refund_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}

  AND mem.member_name LIKE concat('%',#{keyword},'%') OR card.carduid = #{keyword}


  AND mem.canteen_id = #{canteenId}


  AND

    mem.canteen_id = #{canteenId}


ORDER BY ref.refund_time DESC

select mem.id AS m_id,mem.member_id AS m_member_id,mem.member_name AS m_member_name, mem.telephone AS m_telephone,
ref.*,ref.refund_time AS refundTimeStr,CASE ref.refund_type WHEN 0 THEN '线下退款' WHEN 1 THEN '微信退款' WHEN 2 THEN '支付宝退款' END AS refundTypeName,card.carduid AS m_cardUid
FROM dh_member_refundbill ref
LEFT JOIN dh_members mem ON mem.member_id = ref.member_id
LEFT JOIN dh_member_card card ON mem.cardid = card.cardid
where 1=1 AND mem.is_delete = 0 AND DATE_FORMAT(ref.refund_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}

  AND mem.member_name LIKE concat('%',#{keyword},'%') OR card.carduid = #{keyword}


  AND mem.canteen_id = #{canteenId}


  AND

    mem.canteen_id = #{canteenId}


ORDER BY ref.refund_time DESC
limit #{offSet},#{pageSize}
