mapper.select:
select count(1) from dh_members mem LEFT JOIN dh_member_type type ON mem.member_type_id = type.id
LEFT JOIN dh_member_card card ON card.cardid = mem.cardid
where 1 = 1 AND mem.is_delete = 0

    AND mem.member_name LIKE concat('%',#{keyword},'%') OR mem.member_id LIKE concat('%',#{keyword},'%') OR
    mem.telephone LIKE concat('%',#{keyword},'%')


    AND mem.member_type_id = #{typeId}


    AND mem.canteen_id = #{canteenId}


    AND

        mem.canteen_id = #{canteenId,jdbcType=VARCHAR}

select mem.*,type.name AS typeName,CASE mem.is_use when 0 then '禁用' when 1 then '正常' end as statusName, mem.identity_card AS identityCard,mem.leave_name AS leaveName, CASE mem.sex WHEN 0 THEN '男' WHEN 1 THEN '女'END AS sexName ,card.carduid AS ca_carduid,card.accountbalance AS ca_accountbalance from dh_members mem LEFT JOIN dh_member_type type ON mem.member_type_id = type.id LEFT JOIN dh_member_card card ON card.cardid = mem.cardid where 1 = 1 AND mem.is_delete = 0
AND mem.member_name LIKE concat('%',#{keyword},'%') OR mem.member_id LIKE concat('%',#{keyword},'%') OR mem.telephone LIKE concat('%',#{keyword},'%')
AND mem.member_type_id = #{typeId}
AND mem.canteen_id = #{canteenId}
AND
mem.canteen_id = #{canteenId,jdbcType=VARCHAR}
limit #{offSet},#{pageSize}

select mem.member_id AS memberId,mem.member_name AS memberName, mem.telephone,card.accountbalance AS balance,mem.is_use as isUse
from dh_members mem LEFT JOIN dh_member_card card ON card.cardid = mem.cardid
where 1 = 1 AND mem.is_delete = 0 AND  mem.member_id = #{memberId,jdbcType=VARCHAR}

select count(1) from dh_members mem LEFT JOIN dh_member_type type ON mem.member_type_id = type.id
LEFT JOIN dh_member_card card ON card.cardid = mem.cardid
where 1 = 1 AND mem.is_delete = 0

    AND mem.member_name LIKE concat('%',#{memberName},'%') OR mem.member_id LIKE concat('%',#{memberName},'%')


    AND mem.telephone LIKE concat('%',#{memberTel},'%')


    AND card.carduid = #{memberCardUid}


    AND mem.member_type_id = #{typeId}


    AND mem.canteen_id = #{canteenId}

select mem.*,type.name AS typeName,CASE mem.is_use when 0 then '禁用' when 1 then '正常' end as statusName,
CASE mem.sex WHEN 0 THEN '男' WHEN 1 THEN '女'END AS sexName ,card.carduid AS ca_carduid,card.accountbalance AS
ca_accountbalance
from dh_members mem LEFT JOIN dh_member_type type ON mem.member_type_id = type.id
LEFT JOIN dh_member_card card ON card.cardid = mem.cardid
where 1 = 1 AND mem.is_delete = 0

    AND mem.member_name LIKE concat('%',#{memberName},'%') OR mem.member_id LIKE concat('%',#{memberName},'%')


    AND mem.telephone LIKE concat('%',#{memberTel},'%')


    AND card.carduid = #{memberCardUid}


    AND mem.member_type_id = #{typeId}


    AND mem.canteen_id = #{canteenId}

limit #{offSet},#{pageSize}
