mapper.update:
update t_recipe_plan
set is_delete = 1,
  update_user = #{updateUser,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

update t_recipe_plan
set update_user = #{updateUser,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select  count(id)  from t_recipe_plan

where  recipe_plan_id LIKE  #{keyword}


  where  canteen_id =  #{canteenId}

select plan.id,plan.recipe_plan_id,plan.canteen_id,plan.dinner_time,plan.canteen_id, items.recipe_id it_recipe_id,items.recipe_type it_recipe_type,items.surplus it_surplus,items.price_day it_price_day,items.market_price_day it_market_price_day,items.is_hot it_is_hot, recipe.id r_id, recipe.recipe_name r_recipe_name,recipe.recipe_id r_recipe_id,recipe.surplus r_surplus,recipe.guide_price r_guide_price,recipe.market_price r_market_price from t_recipe_plan plan LEFT JOIN t_recipe_planitems items ON plan.recipe_plan_id = items.recipe_plan_id LEFT JOIN t_recipe recipe ON items.recipe_id = recipe.recipe_id WHERE 1=1 AND plan.is_delete = 0
AND plan.recipe_plan_id LIKE #{keyword}
AND plan.canteen_id = #{canteenId}
AND plan.dinner_time BETWEEN #{startdate} AND #{enddate} ORDER BY plan.dinner_time ASC

select plan.id,plan.recipe_plan_id,plan.comment,plan.dinner_time,plan.is_delete,plan.canteen_id,plan.create_time,plan.create_user
from t_recipe_plan plan
/*INNER JOIN t_recipe_planitems items ON plan.recipe_plan_id = items.recipe_plan_id*/
WHERE 1=1 AND plan.is_delete = 0

  AND  plan.canteen_id =  #{canteenId}


  AND

    canteen_id=#{can,jdbcType=VARCHAR}



  AND  plan.dinner_time BETWEEN #{startdate} AND #{enddate}


  AND  plan.dinner_time >= #{now}

ORDER BY plan.dinner_time ASC
limit #{offSet},#{pageSize}

select plan.recipe_plan_id
from t_recipe_plan plan
/*INNER JOIN t_recipe_planitems items ON plan.recipe_plan_id = items.recipe_plan_id*/
WHERE 1=1 AND plan.is_delete = 0

  AND  plan.canteen_id =  #{canteenId}



  AND   plan.dinner_time >= #{startdate}


  AND  plan.dinner_time >= #{now}

ORDER BY plan.dinner_time ASC
limit 1

select count(1) from t_recipe_plan plan  WHERE 1=1 AND plan.is_delete = 0

  AND  plan.canteen_id =  #{canteenId}


  AND

    canteen_id=#{can,jdbcType=VARCHAR}



   AND date_format(plan.dinner_time,'%Y-%m-%d')=#{startdate}


  AND date_format(plan.dinner_time,'%Y-%m-%d')=#{startdate}

select surplus from t_recipe_planitems where recipe_id=#{recipeId,jdbcType=VARCHAR}
         AND recipe_plan_id=#{planId,jdbcType=VARCHAR} AND recipe_type=#{recipeType}
         AND is_delete=0
